import React, {Component} from 'react'
import {BrowserRouter as Router, Route, Switch, Link} from 'react-router-dom'


class TodoApp extends Component {
    render() {
        return (
           <div className="todoApp">

               <Router>
                   <HeaderComponent />

                   <Switch>

                   <Route path="/" exact component={LoginComponent}/>
                   <Route path="/login" component={LoginComponent}/>
                   <Route path="/wellcome/:name" component={WellcomeComponent}/>
                   <Route path="/todos" component={ListTodosComponent}/>
                   <Route component={ErrorComponent}/>

                   </Switch>

                   <FooterComponent />



               </Router>

               {/*<LoginComponent />
               <WellcomeComponent />*/}

           </div>
        )
    }
}

class HeaderComponent extends Component {
    render() {
        return (

            <header>
                <nav className="navbar navbar-expand-md navbar-dark bg-info">
                    <div><a className="navbar-brand">School4You</a></div>

                    <ul className="navbar-nav">
                        <li><Link className="nav-link" to="/Wellcome/ali"></Link>Home</li>
                        <li><Link className="nav-link"to="/todos"></Link>Todos</li>
                    </ul>

                    <ul className="navbar-nav navbar-collapse justify-content-end">
                        <li className="nav-link"><Link to="/login"></Link>Login</li>
                        <li className="nav-link"><Link to="/logout"></Link>Logout</li>
                    </ul>
                </nav>


            </header>


        )
    }
}

class FooterComponent extends Component {
    render() {
        return (
            <div>
                <hr/> Footer

            </div>
        )
    }
}

class ListTodosComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            todos :
            [ 
            {id : 1, description : 'learn React', done : false, targetDate : new Date()},
            {id : 2, description : 'Get drunk', done : false, targetDate : new Date()},
            {id : 3, description : 'Get laid', done : false, targetDate : new Date()}
            ]
        }
    }
    render () {
        return (
            <div>
                <h1>TODO LIST</h1>

                <table>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Description</th>
                            <th>Done</th>
                            <th>Target Date</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                        this.state.todos.map(
                            todo => 
                        <tr>
                            <td>{todo.id}</td>
                            <td>{todo.description}</td>
                            <td>{todo.done.toString()}</td>
                            <td>{todo.targetDate.toString()}</td>
                        </tr>
                        )

                        }
                    </tbody>
                </table>

            </div>
        )
    }
}

class WellcomeComponent extends Component {
    render() {
        return (
        <div> Wellcome {this.props.match.params.name}.  

         Manage your todos <Link to="/todos">here</Link>.

        </div>  

        )
    }
}

class LoginComponent extends Component {

    constructor(props) {
        super(props)
        this.state = {
            username : 'enter username',
            password : '',
            failed : false,
            success : false
        }
    }

    handleChange = (event) => {
        console.log(event.target.name);
        this.setState(
            {
                [event.target.name] : event.target.value
            }
        )
    }

    loginClicked = () => {
        if(this.state.username === 'ali' && this.state.password === 'jemel'){
            this.props.history.push(`/wellcome/${this.state.username}`)
        //this.setState({success : true})
        //this.setState({failed : false})
        }else {
        this.setState({failed : true})
        this.setState({success : false})
        }

    }
    render(){
        return(
            //TRUE && True returns the second term !!!!!!!!!!!!!!!!!!!!!!!!!!!!
            <div>

             {this.state.failed &&  <div>Invalid Creds</div>}
             {this.state.success && <div>Success Login</div>}

             {/*<ShowLoginSuccessMessage ShowSuccessMessage={this.state.success}/>*/}

            User Name : <input type="text" name="username" value={this.state.username} onChange={this.handleChange}></input>

            Password: <input type="password" name="password" value={this.state.password} onChange={this.handleChange}></input>

            <button onClick={this.loginClicked}>Login</button>
            </div>

        )
    }
}

function ErrorComponent() {
    return <div>An error occured</div>
}
// function ShowLoginFailMessage(props) {
//     if(props.ShowFailMessage) {
//        return  <div>EPIC FAIL</div>
//     } 
//     else return null
// }


// function ShowLoginSuccessMessage(props) {
//     if(props.ShowSuccessMessage) {
//        return  <div>Login Success</div>
//     } 
//     else return null
// }


export default TodoApp
