import React, { Component } from 'react'
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom'
import AuthenticationService from './AuthenticationService'
import AuthenticatedRoute from './AuthenticatedRoute.jsx'
import HeaderComponent from './HeaderComponent'
import TodoDataService from '../../api/TodoDataService.js'


class ListTodosComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            todos:
                [
                    // { id: 1, description: 'mathe', done: false, targetDate: new Date() },
                    // { id: 2, description: 'Kunst', done: false, targetDate: new Date() },
                    // { id: 3, description: 'Meeting', done: false, targetDate: new Date() }
                ]
        }
    }

    componentDidMount() {
        let username = AuthenticationService.getLoggedInUserName()
        TodoDataService.retrieveAllTodos(username)
        .then(
            response => {
                this.setState()
            }
        )
        .catch(

        )
        }

    render() {
        return (
            <div>
                <h1>Kommende Termine</h1>

                <div className="container"></div>

                <table className="table">
                    <thead>
                        <tr>
                            <th>Description</th>
                            <th>Done</th>
                            <th>Target Date</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.todos.map(
                                todo =>
                                    <tr key={todo.id}>
                                        <td>{todo.description}</td>
                                        <td>{todo.done.toString()}</td>
                                        <td>{todo.targetDate.toString()}</td>
                                    </tr>
                            )

                        }
                    </tbody>
                </table>

                <div />

            </div>
        )
    }
}
export default ListTodosComponent
