        if (this.state.username === 'ali' && this.state.password === 'jemel') {

            AuthenticationService.registerSuccessfullLogin(this.state.username, this.state.password);
            this.props.history.push(`/wellcome/${this.state.username}`)
            //this.setState({success : true})
            //this.setState({failed : false})
        } else {
            this.setState({ failed: true })
            this.setState({ success: false })
        }

    }
    render() {
        return (
            //TRUE && True returns the second term !!!!!!!!!!!!!!!!!!!!!!!!!!!!
            <div className="container-fluid bg">
                <div className="row">
                    <div className="col-md-4 col-sm-4 col-xs-12"></div>
                    <div className="col-md-4 col-sm-4 col-xs-12">
                        <form className="form-container">
                            <div class="form-group">
                                <label for="exampleInputEmail1">Email addresse</label>
                                <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Email"></input>
                                <small id="emailHelp" class="form-text text-muted"></small>
                            </div>

                            <div class="form-group">
                                <label for="exampleInputPassword1">Passwort</label>
                                <input type="password" class="form-control" id="exampleInputPassword1" placeholder=""></input>
                                <small id="emailHelp" class="form-text text-muted">Passwort vergessen?</small>

