import React, {Component} from 'react'
import {BrowserRouter as Router, Route, Switch, Link} from 'react-router-dom'
import AuthenticationService from './AuthenticationService'
import AuthenticatedRoute from './AuthenticatedRoute.jsx'
import HeaderComponent from './HeaderComponent'


class TodoApp extends Component {
    render() {
        return (
           <div className="todoApp">

               <Router>
                   <HeaderComponent />

                   <Switch>

                   <Route path="/" exact component={LoginComponent}/>
                   <Route path="/login" component={LoginComponent}/>
                   <AuthenticatedRoute path="/wellcome/:name" component={WellcomeComponent}/>
                   <AuthenticatedRoute path="/todos" component={ListTodosComponent}/>
                   <AuthenticatedRoute path="/logout" component={LogoutComponent}/>
                   <Route component={ErrorComponent}/>

                   </Switch>

                   <FooterComponent />



               </Router>

               {/*<LoginComponent />
               <WellcomeComponent />*/}

           </div>
        )
    }
}


class FooterComponent extends Component {
    render() {
        return (
            <footer className="footer">
                <span className="text-muted">All rights Reserved 2020 Gruppe4</span>
            </footer>
        )
    }
}

class LogoutComponent extends Component {
    render() {
        return (
            <div>
                <h1>You are Logged out</h1>
                <div className = "container">
                    Thank you for using out application
                </div>

            </div>
        )
    }
}

class ListTodosComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            todos :
            [ 
            {id : 1, description : 'learn React', done : false, targetDate : new Date()},
            {id : 2, description : 'Get drunk', done : false, targetDate : new Date()},
            {id : 3, description : 'Get laid', done : false, targetDate : new Date()}
            ]
        }
    }
    render () {
        return (
            <div>
                <h1>Kommende Termine</h1>

               <div className="container"></div>

                <table className="table">
                    <thead>
                        <tr>
                            <th>Description</th>
                            <th>Done</th>
                            <th>Target Date</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                        this.state.todos.map(
                            todo => 
                        <tr key={todo.id}>
                            <td>{todo.description}</td>
                            <td>{todo.done.toString()}</td>
                            <td>{todo.targetDate.toString()}</td>
                        </tr>
                        )

                        }
                    </tbody>
                </table>

                <div/>

            </div>
        )
    }
}

class WellcomeComponent extends Component {
    render() {
        return (
        <div>
            <h1>Willkommen</h1> 
            <div className="container">
            Willkommen {this.props.match.params.name}. 
            </div>

         Verwalte dein Profil  <Link to="/todos">hier</Link>.

        </div>  

        )
    }
}

class LoginComponent extends Component {

    constructor(props) {
        super(props)
        this.state = {
            username : 'enter username',
            password : '',
            failed : false,
            success : false
        }
    }

    handleChange = (event) => {
        console.log(event.target.name);
        this.setState(
            {
                [event.target.name] : event.target.value
            }
        )
    }

    loginClicked = () => {
        if(this.state.username === 'ali' && this.state.password === 'jemel'){

            AuthenticationService.registerSuccessfullLogin(this.state.username, this.state.password);
            this.props.history.push(`/wellcome/${this.state.username}`)
        //this.setState({success : true})
        //this.setState({failed : false})
        }else {
        this.setState({failed : true})
        this.setState({success : false})
        }

    }
    render(){
        return(
            //TRUE && True returns the second term !!!!!!!!!!!!!!!!!!!!!!!!!!!!
         <div>

                <h1>Login</h1>
                <div className="container">
                <form className="main-form">








                </form>

             {this.state.failed &&  <div className="alert alert-warning">Invalid Creds</div>}
             {this.state.success && <div>Success Login</div>}

             {/*<ShowLoginSuccessMessage ShowSuccessMessage={this.state.success}/>*/}

            User Name : <input type="text" name="username" value={this.state.username} onChange={this.handleChange}></input>

            Password: <input type="password" name="password" value={this.state.password} onChange={this.handleChange}></input>

            <button className="btn btn-success " onClick={this.loginClicked}>Login</button>
              </div>



        </div>

        )
    }
}

function ErrorComponent() {
    return <div>An error occured</div>
}
// function ShowLoginFailMessage(props) {
