import React, {Component} from 'react'


class TodoApp extends Component {
    render() {
        return (
           <div className="todoApp">
               My   Todo Application

               <LoginComponent></LoginComponent>

           </div>
        )
    }
}

class LoginComponent extends Component {

    constructor(props) {
        super(props)
        this.state = {
            username : 'enter username',
            password : ''
        }
    }

    handleUsernameChange = (event) => {
        console.log(event.target.value);
        this.setState(
            {
                username : <event className="target value"></event>
            }
        )


    }
    render(){
        return(
            <div>
            User Name : <input type="text" name="username" value={this.state.username} onChange={this.handleUsernameChange}></input>

            Password: <input type="password" name="password" value={this.state.password}></input>

            <button>Login</button>
            </div>

        )
    }
}
export default TodoApp
